const gulp = require('gulp');
const config = require('./config');
const mocha = require('gulp-spawn-mocha');

gulp.task('int', () => {
  return gulp.src(config.tests.integration, { read: false })
    .pipe(mocha({
      reporter: 'dot',
      ui: 'mocha-given',
      require: ['coffee-script/register', 'should', 'should-sinon']
    }));
});

